Action.Str:
/* Format YouTube API duration (formatted
as PTxHxMxS) as h:mm:ss or m:ss */

let duration = par[0];
duration = duration.replace(/[hm]/gi, ':');
duration = duration.replace(/[pts]/gi, '');

// insert 00 seconds if not present from API
if (!par[0].includes('S')) {
    duration = `${duration}00`;
}

// create array of h/m/s values
let timeUnits = duration.split(':');

// starting with index 1, modify time units
// to 0x if unit is <10 and not equal to 0
for (let i = 1; i < timeUnits.length; i++) {
    let currentUnitNum = Number(timeUnits[i]);
    if (currentUnitNum < 10 && currentUnitNum !== 0) {
        timeUnits[i] = `0${timeUnits[i]}`;
    }
}

// if over 1 hour and no mins present, set mins to 00
if(par[0].includes('H') && !par[0].includes('M')) {
    timeUnits.splice(1, 0, '00');
}

var formatted_duration = timeUnits.join(':');
